function onSubmit() {

	var Cfaturamento = g_form.getValue('faturamento').replace(/\./g,"").replace("$ ","");
	g_form.setValue('faturamento',"111");

	var Cglosa = g_form.getValue('glosa').replace(/\./g,"").replace("$ ","");
	g_form.setValue('glosa',"111");

	var CValorAnual = g_form.getValue('valor_anual_do_contrato').replace(/\./g,"").replace("$ ","");
	g_form.setValue('valor_anual_do_contrato',"111");

	var CValorMensal = g_form.getValue('valor_mensal_do_contrato').replace(/\./g,"").replace("$ ","");	
	g_form.setValue('valor_mensal_do_contrato',"111");

	alert(g_form.getValue('valor_mensal_do_contrato') + "\n" + g_form.getValue('valor_anual_do_contrato') + "\n" +  g_form.getValue('faturamento') + "\n" + g_form.getValue('glosa'));

}
