catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	if (isPositiveInt(newValue)){
		g_form.hideFieldMsg('indicadores_acima_da_meta');
		g_form.clearMessages();

	}else{
		g_form.clearMessages();
		g_form.hideFieldMsg('indicadores_acima_da_meta');
		g_form.showFieldMsg('indicadores_acima_da_meta',"Valor Inválido!","error");
		g_form.addErrorMessage("Valor informado não é válido");
	}		
}

function isPositiveInt(value) {
	return !isNaN(value) &&
	parseInt(Number(value)) == value &&
	!isNaN(parseInt(value, 10)) &&
	parseInt(Number(value)) >= 0;
}
