catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	//var date1 = new Date(newValue);
	var datearray = newValue.split("/");
	var newdate = datearray[1] + '/' + datearray[0] + '/' + datearray[2];

	var datainicial = new Date(newdate);
	var dataatual = new Date();

	//Verifica se a data inicial informada é anterior a data atual
 	var timeDiff = datainicial.getTime() -  dataatual.getTime();
  	if (timeDiff > -86400000){	
  		g_form.clearMessages();
  		g_form.hideFieldMsg('initial_date');
  		g_form.showFieldMsg('initial_date',"O período de aferição deve ser anterior a data atual","error");
  		g_form.addErrorMessage("Valor informado não é válido");	
  	}else{
		g_form.hideFieldMsg('initial_date');
		g_form.clearMessages();
	}

	//Verifica se a data final está preeenchida e se a data inicial é anterior a data final.
	if (g_form.getValue('final_date') != ''){
		var datasplitted = g_form.getValue('final_date').split('/');
		newdate = datasplitted[1] + '/' + datasplitted[0] + '/' + datasplitted[2];
		var datafinal = new Date(newdate);

		if (datainicial.getTime() > datafinal.getTime()){
			g_form.hideFieldMsg('initial_date');
			  g_form.showFieldMsg('initial_date',"A data inicial não pode ser posterior a data final","error");
		}
	}
}
